import React, { Component, ChangeEvent, FormEvent } from 'react';
import { withRouter, RouteComponentProps } from 'react-router-dom';

import DashboardHeader from 'components/fragments/DashboardHeader';

import Status from 'types/StatusType';

import 'styles/dashboard.css';

export interface DispatchProps {
  //getMovies: Function;
  getInitialCoordinates: Function;
}

export interface StateProps {
  //moviesData: MoviesAllDataContentType;
}

export interface State {
  generationNb: number;
  inputText: String;
}

type Props = {} & DispatchProps & StateProps & RouteComponentProps;

class Dashboard extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = {
      generationNb: 0,
      inputText: '',
    };
  }

  generateMap = () => {
    return (
      <div className="DashboardMapCase">
      </div>
    )
  }

  showFile = async (e : any) => {
    const { getInitialCoordinates } = this.props;
    e.preventDefault();

    const reader = new FileReader();

    reader.onload = async (e) => {
      if (e.target) {
        const text = (e.target.result)
        if (text) {
          getInitialCoordinates();
        }
      }
    }
    reader.readAsText(e.target.files[0]);
  }

  render() {
    //const { moviesData } = this.props;
    const { generationNb, inputText } = this.state;

    return (
      <div>
        <DashboardHeader />
        <div className="DashboardContainer">
          <div className="DashboardButtonBarContainer">
            <label>
              Importer mon fichier
              <input className="DashboardButton" type='file' onChange={this.showFile} value="Importer mon fichier"/>
            </label>

            <button className="DashboardButton">Exporter</button>
            <button className="DashboardButton">Prochaine génération</button>
            <button className="DashboardButton">Auto Play</button>
          </div>
          <div className="DashboardGeneration">
            <p>Génération numéro: {generationNb}</p>
          </div>
          {this.generateMap()}
        </div>
      </div>
    );
  }
}

export default withRouter(Dashboard);
